package amit.springframework.amitpetclinic_New.model;import lombok.*;import javax.persistence.*;import java.time.LocalDate;/** * created by KUAM on 4/25/2020 */@Setter@Getter@NoArgsConstructor@AllArgsConstructor@Builder@Entity@Table(name = "visits")public class Visit extends BaseEntity{    @Column(name = "local_date")    private LocalDate date;    @Column(name = "description")    private String description;    @ManyToOne    @JoinColumn(name = "pet_id")    private Pet pet;}